import React, { useEffect, useState } from "react";
import { useSelector } from "react-redux";
import { Grid, Paper } from "@material-ui/core";
import { makeStyles, Theme, createStyles } from "@material-ui/core/styles";
import { store } from "../../reducers/types";

const useStyles = makeStyles((theme: Theme) => {
  createStyles({
    root: {
      flexGrow: 1
    },
    paper: {
      padding: theme.spacing(2),
      textAlign: "center",
      color: theme.palette.text.secondary
    }
  });
});

const PostListContainer = () => {
  const classes = useStyles({});
  const { posts } = useSelector((state: store) => state.post);
  const [devPost, setDevPost] = useState(null);
  const [financePost, setFinancePost] = useState(null);
  const [hackingPost, setHackingPost] = useState(null);
  const [businessPost, setBusinessPost] = useState(null);

  useEffect(() => {
    if (posts) {
      posts.map(post => {
        if (post.category === "dev") {
          setDevPost(state => {
            return [...state, post];
          });
        }
      });
    }
  }, [posts]);

  return (
    <div className={classes.root}>
      <Grid container spacing={3}>
        <Grid item xs={12}>
          <Paper className={classes.paper}>dev</Paper>
          {}
        </Grid>
      </Grid>
    </div>
  );
};

export default PostListContainer;
