import { makeStyles, createStyles, Theme } from "@material-ui/core/styles";

export const useStyles = makeStyles((theme: Theme) =>
  createStyles({
    formControl: {
      marign: theme.spacing(1),
      minWidth: 120,
      maxWidth: 320
    },
    title: {
      width: "100%"
    },
    markdownTextArea: {
      width: 400,
      height: 500,
      marginTop: "100px"
    },
    textAreaContainer: {
      flex: 1,
      width: "100%",
      display: "inline"
    },
    markdownPreview: {
      display: "inline-block"
    }
  })
);
