import React from "react";
import { Breadcrumbs, Link } from "@material-ui/core";
import { NextPage } from "next";

type props = {
  title?: string | string[];
  category?: string | string[];
};

const Breadcrumb = (props: props) => {
  const LinkComponent = ({ title = "", category = "" }) => {
    let Component = () => (
      <Link color="inherit" href="/">
        home
      </Link>
    );
    if (category) {
      Component = () => (
        <>
          <Component />
          <Link color="inherit" href={`/${category}`}>
            {category}
          </Link>
        </>
      );
    }
    if (title) {
      Component = () => (
        <>
          <Component />
          <Link color="inherit" href={`/${category}/${title}`}>
            title
          </Link>
        </>
      );
    }
    return Component();
  };

  return (
    <Breadcrumbs aria-label="breadcrumb">
      {
        //@ts-ignore
        <LinkComponent title={props.title} category={props.category} />
      }
    </Breadcrumbs>
  );
};

export default Breadcrumb;
