import React, { useEffect, Context } from "react";

import { useRouter } from "next/router";
import { useSelector, useDispatch } from "react-redux";
import {
  GET_POST_REQUEST,
  FETCH_POST_REQUEST
} from "../../../reducers/post/actions";
import { store } from "../../../reducers/types";
import Renderer from "../../../Components/draft/Renderer";
import { List, ListItem, ListItemText, Grid } from "@material-ui/core";
import { makeStyles, Theme, createStyles } from "@material-ui/core/styles";

const useStyles = makeStyles((theme: Theme) =>
  createStyles({
    root: {
      width: "100%",
      maxWidth: 360,
      backgroundColor: theme.palette.background.paper
    }
  })
);

const Post = () => {
  const classes = useStyles({});
  const { currentPost } = useSelector((state: store) => state.post);
  const { isPostLoading } = useSelector(
    (state: store) => state.post.loadingStates
  );
  if (isPostLoading) {
    return (
      <>
        <div>로딩중 입니다!</div>
      </>
    );
  } else {
    return (
      <div className={classes.root}>
        <Grid container>
          <Grid Item xs={6}>
            <List>
              <ListItem>
                <ListItemText>{currentPost.title}</ListItemText>
              </ListItem>
            </List>
          </Grid>
          <Grid Item xs={6}>
            <List>
              <ListItem>
                <ListItemText primary={currentPost.nickname} />
                <ListItemText primary={currentPost.Date} />
              </ListItem>
            </List>
          </Grid>
        </Grid>

        {currentPost && <Renderer raw={JSON.parse(currentPost.body)} />}
      </div>
    );
  }
};

Post.getInitialProps = ctx => {
  const { title, category } = ctx.query;

  ctx.store.dispatch({
    type: FETCH_POST_REQUEST,
    payload: {
      title,
      category
    }
  });
  return {};
};

export default Post;
