import React, { Component } from "react";
import { EditorState, convertToRaw, convertFromRaw } from "draft-js";
import { Editor } from "react-draft-wysiwyg";
//load editor css
import "../../../node_modules/react-draft-wysiwyg/dist/react-draft-wysiwyg.css";

function uploadImageCallBack(file) {
  return new Promise((resolve, reject) => {
    const xhr = new XMLHttpRequest();
    xhr.open("POST", "https://api.imgur.com/3/image");
    xhr.setRequestHeader("Authorization", "Client-ID b1e6509aad56ae0");
    const data = new FormData();
    data.append("image", file);
    xhr.send(data);
    xhr.addEventListener("load", () => {
      const response = JSON.parse(xhr.responseText);
      resolve(response);
    });
    xhr.addEventListener("error", () => {
      const error = JSON.parse(xhr.responseText);
      reject(error);
    });
  });
}

class MakePostsForm extends Component {
  constructor(props) {
    super(props);

    const content = window.localStorage.getItem("content");
    if (content) {
      this.state = {
        editorState: EditorState.createWithContent(
          convertFromRaw(JSON.parse(content))
        )
      };
    } else {
      this.state = {
        editorState: EditorState.createEmpty()
      };
    }
  }

  onChangeEditorState: Function = editorState => {
    const content = this.setState({ editorState });
  };

  render() {
    return (
      <Editor
        //@ts-ignore
        editorState={this.state.editorState}
        editorClassName="demo-editor"
        onEditorStateChange={this.onChangeEditorState}
        localization={{ locale: "ko" }}
        toolbar={{
          image: {
            uploadCallback: uploadImageCallBack,
            alt: { present: true, mandatory: true }
          }
        }}
      />
    );
  }
}

export default MakePostsForm;
