import React, { useState } from "react";
import { NextPage } from "next";
import {
  FormControl,
  InputLabel,
  Input,
  Typography,
  TextField
} from "@material-ui/core";

type props = {
  text: string;
  handleText: any;
};

const MakePostsForm: NextPage<props> = ({ text, handleText }) => {
  const [title, setTitle] = useState("");

  const _onChangeTitle = e => {
    setTitle(e.target.value);
  };

  return (
    <div>
      <Typography component="h1" variant="h5">
        게시글 작성하기
      </Typography>
      <FormControl margin="normal">
        <InputLabel>제목</InputLabel>
        <Input
          placeholder="제목"
          value={title}
          onChange={_onChangeTitle}
          fullWidth
        />
        <TextField
          variant="standard"
          value={text}
          onChange={handleText}
          autoFocus
          fullWidth
        />
      </FormControl>
    </div>
  );
};

export default MakePostsForm;
