import React, { useEffect, Context } from "react";
import { NextPage, NextPageContext } from "next";
import { useRouter } from "next/router";
import { useSelector, useDispatch } from "react-redux";
import { GET_POSTS_REQUEST } from "../../reducers/post/actions";
import { store } from "../../reducers/types";
import { Link, Grid, Typography, Divider, Paper } from "@material-ui/core";
import { makeStyles, Theme, createStyles } from "@material-ui/core/styles";

const useStyles = makeStyles((theme: Theme) =>
  createStyles({
    root: {
      flexGrow: 1
    },
    paper: {
      padding: theme.spacing(2),
      margin: "auto",
      maxWidth: 500
    },
    image: {
      width: 128,
      height: 128
    },
    img: {
      margin: "auto",
      display: "block",
      maxWidth: "100%",
      maxHeight: "100%"
    }
  })
);

const Post = () => {
  const { posts } = useSelector((state: store) => state.post);
  const router = useRouter();
  const { category } = router.query;

  const classes = useStyles({});

  return (
    <>
      <Grid container justify="center" spacing={3} className={classes.roout}>
        <Grid item xs={12}>
          <Typography>{category}</Typography>
        </Grid>
        <Divider />
        {posts &&
          posts.map(post => {
            return (
              <>
                <Link href={`/post/${category}/${post.title}`} key={post._id}>
                  <Grid key={post._id} item xs={6}>
                    <Paper>
                      <Typography>{post.title}</Typography>
                      <Typography>{post.nickname}</Typography>
                      <Typography>{post.Date}</Typography>
                    </Paper>
                  </Grid>
                </Link>
              </>
            );
          })}
      </Grid>
    </>
  );
};

Post.getInitialProps = async ctx => {
  const { category } = ctx.query;
  ctx.store.dispatch({
    type: GET_POSTS_REQUEST,
    payload: { category }
  });
};

export default Post;
