import React, { useState } from "react";
import { NextPage } from "next";
import {
  FormControl,
  InputLabel,
  Input,
  Typography,
  TextField,
  Button,
  Select,
  MenuItem,
  FormLabel
} from "@material-ui/core";
import { useSelector } from "react-redux";
import { store } from "../../../reducers/types";
import CircularProgressComponent from "../../../utils/Components/CircularProgressComponent";

type props = {
  text: string;
  handleText: any;
};

const MakePostsForm: NextPage<props> = ({ text, handleText }) => {
  const [title, setTitle] = useState("");
  const [category, setCategory] = useState("");
  const { isPosting } = useSelector((state: store) => state.post.loadingStates);

  const _onCategoryChange = (e: React.ChangeEvent<{ value: string }>) => {
    setCategory(e.target.value);
  };

  const _onChangeTitle = (e: React.ChangeEvent<{ value: string }>) => {
    setTitle(e.target.value);
  };

  return (
    <div>
      <Typography component="h1" variant="h5">
        게시글 작성하기
      </Typography>
      <FormControl margin="normal">
        <TextField
          id="outlined-basic"
          label="제목"
          variant="outlined"
          value={title}
          onChange={_onChangeTitle}
        />
        <FormLabel component="legend">카테고리</FormLabel>
        <Select value={category} onChange={_onCategoryChange}>
          <MenuItem value="dev">개발</MenuItem>
          <MenuItem value="hacking">해킹</MenuItem>
          <MenuItem value="finance">재테크</MenuItem>
          <MenuItem value="business">비즈니스</MenuItem>
        </Select>
        <TextField
          variant="standard"
          value={text}
          onChange={handleText}
          autoFocus
          fullWidth
        />
        <Button
          type="submit"
          fullWidth
          variant="contained"
          color="primary"
          disabled={isPosting}
        >
          {isPosting ? (
            <CircularProgressComponent />
          ) : (
            <Typography>포스팅</Typography>
          )}
        </Button>
      </FormControl>
    </div>
  );
};

export default MakePostsForm;
