import React, { useState } from "react";
import { NextPage } from "next";
import {
  FormControl,
  InputLabel,
  Input,
  Typography,
  TextField,
  Button
} from "@material-ui/core";
import { useSelector } from "react-redux";
import { store } from "../../../reducers/types";
import CircularProgressComponent from "../../../utils/Components/CircularProgressComponent";

type props = {
  text: string;
  handleText: any;
};

const MakePostsForm: NextPage<props> = ({ text, handleText }) => {
  const [title, setTitle] = useState("");

  const { isPosting } = useSelector((state: store) => state.post.loadingStates);

  const _onChangeTitle = e => {
    setTitle(e.target.value);
  };

  return (
    <div>
      <Typography component="h1" variant="h5">
        게시글 작성하기
      </Typography>
      <FormControl margin="normal">
        <InputLabel>제목</InputLabel>
        <Input
          placeholder="제목"
          value={title}
          onChange={_onChangeTitle}
          fullWidth
        />
        <TextField
          variant="standard"
          value={text}
          onChange={handleText}
          autoFocus
          fullWidth
        />
        <Button
          type="submit"
          fullWidth
          variant="contained"
          color="primary"
          disabled={isPosting}
        >
          {isPosting ? (
            <CircularProgressComponent />
          ) : (
            <Typography>포스팅</Typography>
          )}
        </Button>
      </FormControl>
    </div>
  );
};

export default MakePostsForm;
