import React, { useEffect, Context } from "react";

import { useRouter } from "next/router";
import { useSelector, useDispatch } from "react-redux";
import { GET_POST_REQUEST } from "../../../reducers/post/actions";
import { store } from "../../../reducers/types";

const Post = () => {
  const { currentPost } = useSelector((state: store) => state.post);

  return (
    <>
      <h1>{currentPost && currentPost.title}</h1>
    </>
  );
};

Post.getInitialProps = ctx => {
  const { title, category } = ctx.query;
  ctx.store.dispatch({
    type: GET_POST_REQUEST,
    payload: {
      title,
      category
    }
  });

  return {};
};

export default Post;
